// generateInvoicePDF.js
import pdfMake from 'pdfmake/build/pdfmake';
import { vfs } from 'pdfmake/build/vfs_fonts';

pdfMake.vfs = vfs;

// Replace this with your actual PNG background Base64 string
const backgroundBase64 = 'data:image/png;base64,';

// Generate PDF function
export const generatePDF = (invoiceData) => {

  const docDefinition = {
    // Add the PNG background
    background: [
      {
        image: backgroundBase64,
        width: 595, // A4 width in points
        height: 842, // A4 height in points
        absolutePosition: { x: 0, y: 0 },
      },
    ],
    content: [
      {
        text: 'FutureWatt',
        fontSize: 32,
        alignment: 'center',
        color: '#FF6600',
        margin: [0, 120, 0, 0],
      },
      {
        text: 'Powering a sustainable future with reliable and affordable solar solutions.',
        fontSize: 14,
        alignment: 'center',
        margin: [0, 0, 0, 10],
      },
      {
        columns: [
          {
            text: [
              { text: 'Email: ' },
              'support@futurewatt.com\n',
              { text: 'Phone: ' },
              '+94 123 456 789\n',
              { text: 'Location: ' },
              'Colombo, Sri Lanka\n',
            ],
            width: '50%',
            fontSize: 12,
            color: '#333',
            margin: [0, 30, 0, 0],
          },
          {
            text: 'Invoice',
            fontSize: 24,
            alignment: 'right',
            color: '#FF6600',
            width: '50%',
            
          },
        ],
      },
      {
        text: 'Invoice Details',
        style: 'header',
        margin: [0, 40, 0, 5],
      },
      {
        table: {
          widths: ['*', 'auto'],
          body: [
            ['Order ID:', invoiceData.orderId],
            ['Customer Name:', invoiceData.customerName],
            ['Email:', invoiceData.email],
            ['Phone Number:', invoiceData.phoneNumber],
            ['Delivery Location:', invoiceData.deliveryLocation],
            ['Price (LKR):', invoiceData.price],
          ],
        },
        layout: 'lightHorizontalLines',
        margin: [0, 10, 0, 20],
      },
      {
        text: 'Thank You for Choosing FutureWatt!',
        alignment: 'center',
        fontSize: 14,
        margin: [0, 10, 0, 0],
        color: '#FF6600',
      },
    ],
    styles: {
      header: {
        fontSize: 18,
        color: '#FF6600',
        margin: [0, 10, 0, 5],
      },
    },
  };

  // Generate and open the PDF in a new tab
  pdfMake.createPdf(docDefinition).open();
};